@import url("https://fonts.googleapis.com/css2?family=Work+Sans:wght@400;600&display=swap");
:root {
  --color-text: hsl(0, 0%, 0%);
  --color-text-light: hsl(0, 0%, 45%);
  --color-body: hsl(0, 0%, 100%);
  --color-body-dim: hsl(0, 0%, 95%);
  --color-border-ui: hsl(0, 0%, 83%);
  --color-primary: hsl(207, 90%, 43%);
  --color-primary-light: hsl(208, 37%, 90%);
  --ui-filters-width: 80px;
  --ui-gutter: 5%;
  --ui-radius: 6px;
  --ui-tray-height: 70px;
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: "Work Sans", sans-serif;
  font-weight: 400;
  color: var(--color-text);
  background: var(--color-body);
}

a {
  color: var(--color-primary);
}

header {
  top: 0;
  position: sticky;
  color: var(--color-text);
  background: var(--color-body);
  padding: 15px 15px 15px var(--ui-gutter);
  display: grid;
  grid-template-columns: 0.5fr 1fr;
  border-bottom: 1px solid var(--color-border-ui);
  z-index: 10;
}

h1 {
  display: inline-flex;
  align-items: center;
  font-size: clamp(2vw, 24px, 5vw);
  line-height: 1;
  position: relative;
  cursor: pointer;
}

h1:hover {
  color: var(--color-primary);
}

input,
input::placeholder,
input:placeholder-shown {
  color: var(--color-text);
  background: var(--color-body-dim);
}

input:-webkit-autofill {
  background: var(--color-body-dim);
  -webkit-text-fill-color: var(--color-text);
  box-shadow: 0 0 0 2000px var(--color-body-dim) inset;
}

/* 
    Icons
*/
.icon:hover {
  stroke: var(--color-primary);
}

svg.icon {
  pointer-events: none;
  stroke-width: 1;
}

svg.icon .fill {
  fill: var(--color-text);
  stroke: none;
}

svg.icon .stroke {
  fill: none;
  stroke: var(--color-text);
}

/*
    Link Style
*/
a.underline {
  display: block;
  text-decoration: none;
  display: inline;
  background-image: linear-gradient(to bottom, var(--color-text) 0%, var(--color-text) 100%);
  background-position: 100% 100%;
  background-repeat: no-repeat;
  background-size: 0% 1px;
  transition: background-size 0.3s ease 0s;
}

a.underline:hover {
  text-decoration: none;
  background-position: 0 100%;
  background-size: 100% 1px;
  transition: background-size 0.3s ease 0s;
}

a.underline:visited {
  text-decoration: line-through;
}

@media only screen and (max-width: 767px) {
  :root {
    --ui-gutter: 10px;
  }

  body {
    background: var(--color-body-dim);
  }

  header {
    grid-template-columns: 1fr;
  }

  h1 {
    display: none;
  }
}
/*
    News Cards
*/
.news {
  display: flex;
  margin-bottom: 30px;
  padding-bottom: 30px;
  border-bottom: 1px solid var(--color-border-ui);
  position: relative;
}

.news-image {
  width: 50%;
  overflow: hidden;
  border-radius: var(--ui-radius);
}

.news-image img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
  transition: all 0.6s ease;
}

.news-image:hover img {
  transform: scale(1.025);
}

.news-text {
  padding: 30px 0 30px 30px;
}

/* For news cards WITH images */
.news.has-image .news-text {
  width: 50%;
}

/* For news cards WITHOUT images */
.news:not(.has-image) .news-text {
  width: 100%;
  display: flex;
  padding-left: 0;
}

.news:not(.has-image) .news-text .news-headline {
  width: 50%;
  padding-right: 10%;
}

.news:not(.has-image) .news-text .news-details {
  width: 50%;
  padding-left: 30px;
}

/* Without description */
.news:not(.has-image) .news-text.no-desc {
  flex-direction: column;
}

.news:not(.has-image) .news-text.no-desc .news-headline {
  width: 100%;
  padding-right: 0;
}

.news:not(.has-image) .news-text.no-desc .news-details {
  width: 100%;
  padding-left: 0;
}

.news-title a {
  color: inherit;
}

.news-meta {
  display: flex;
  margin-bottom: 10px;
  line-height: 1;
  align-items: center;
  color: var(--color-text-light);
  font-size: small;
}

.news-categories:empty ~ .separator {
  display: none;
}

.news .separator {
  display: inline-block;
  margin: 0 5px;
}

.news-category {
  color: var(--color-primary);
  text-transform: uppercase;
  font-weight: bold;
  letter-spacing: 0.1em;
  cursor: pointer;
  pointer-events: all;
}

.news-category.underline {
  background-image: linear-gradient(to bottom, var(--color-primary) 0%, var(--color-primary) 100%);
}

.news-category:hover {
  text-decoration: underline;
}

.news-pubdate {
  text-transform: capitalize;
  white-space: nowrap;
}

.news-title {
  font-weight: 700;
  margin-bottom: 10px;
}

.news-desc {
  color: var(--color-text-light);
  line-height: 1.5;
  margin-bottom: 15px;
  word-break: break-word;
}

.news-desc:empty {
  display: none;
}

.news-footer {
  font-size: small;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.news-author {
  color: var(--color-text-light);
  display: flex;
  align-items: center;
}

.news-author svg {
  stroke: var(--color-text-light);
  margin-right: 5px;
}

.news-footer a {
  display: flex;
  align-items: center;
  color: var(--color-primary);
  text-decoration: none;
}

.news-footer svg {
  stroke-width: 1;
  fill: none;
}

.news-link svg {
  stroke: var(--color-primary);
  margin-left: 5px;
  width: 12px;
  height: 12px;
}

.news-footer .news-link {
  background-image: linear-gradient(to bottom, var(--color-primary) 0%, var(--color-primary) 100%);
}

.news-footer .news-link svg {
  transition: transform 0.3s ease;
}

.news-footer .news-link:hover svg {
  transform: translate3d(3px, -3px, 0);
}

/* Intersection Observer class state */
.news {
  transition: opacity 0.3s ease-out, transform 0.6s ease-out;
  transform: scale(0.975);
  transform-origin: 50% 100%;
  opacity: 0;
}

.news.is-visible {
  opacity: 1;
  transform: scale(1);
}

/*
    Media Query
*/
@media only screen and (max-width: 767px) {
  .news {
    margin-bottom: 20px;
    padding-bottom: 20px;
  }

  /* Hero News Card */
  .news.has-image:nth-child(-n+2) {
    flex-direction: column;
  }

  .news.has-image:nth-child(-n+2) .news-image {
    width: 100%;
  }

  .news.has-image:nth-child(-n+2) .news-text,
.news:not(.has-image):nth-child(-n+2) .news-text {
    width: 100%;
    padding: 20px 0 10px;
  }

  /* Cards starting from the second card, 2 and beyond*/
  .news.has-image:nth-child(n+3) .news-image {
    width: 120px;
    height: 120px;
  }

  .news.has-image:nth-child(n+3) .news-text {
    padding: 0 0 0 15px;
    width: calc(100% - 120px);
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    justify-content: center;
  }

  .news.has-image:nth-child(n+3) .news-meta {
    margin-bottom: 5px;
  }

  .news.has-image:nth-child(n+3) .news-meta,
.news.has-image:nth-child(n+3) .news-footer {
    font-size: x-small;
  }

  .news.has-image:nth-child(n+3) .news-category:nth-of-type(n + 2) {
    display: none;
  }

  .news.has-image:nth-child(n+3) .news-category:nth-of-type(n + 1) + .separator {
    display: none;
  }

  .news.has-image:nth-child(n+3) .news-title {
    font-size: 1em;
    margin-bottom: 5px;
  }

  .news.has-image:nth-child(n+3) .news-desc {
    display: none;
  }

  .news.has-image:nth-child(n+3) .news-footer .news-link {
    display: none;
  }

  /* Without Image */
  .news:not(.has-image) .news-text {
    flex-direction: column;
    padding: 0;
  }

  .news:not(.has-image) .news-text .news-headline {
    width: 100%;
    padding-right: 0;
  }

  .news:not(.has-image) .news-text .news-details {
    width: 100%;
    padding-left: 0;
  }
}
/* 
    Filters
*/
.news-filters {
  padding: 15px;
  position: fixed;
  top: 80px;
  right: 0;
  bottom: 0;
  width: var(--ui-filters-width);
  border-left: 1px solid var(--color-border-ui);
  display: flex;
  flex-direction: column;
  align-items: center;
  z-index: 1;
}

#filter-button {
  position: absolute;
  right: 15px;
  width: 48px;
  height: 48px;
  border-radius: var(--ui-radius);
  background: transparent;
  border: 1px solid transparent;
  border: none;
  outline: none;
  cursor: pointer;
}

#filter-button:focus {
  border: 1px solid var(--color-primary);
}

#filter-button:focus .icon {
  stroke: var(--color-primary);
}

.filters-tray {
  display: none;
}

.filter-tray-button {
  width: 100%;
  background: var(--color-body);
  color: var(--color-text);
  border: none;
  padding: 0;
  margin: 0;
  outline: none;
  font-size: x-small;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}

.filter-tray-button svg {
  pointer-events: none;
  margin-bottom: 5px;
}

/*
    Active State
*/
.filter-tray-button.active {
  border-top: 1px solid var(--color-primary);
  color: var(--color-primary);
  background: var(--color-primary-light);
}

.filter-tray-button.active svg .fill {
  fill: var(--color-primary);
}

.filter-tray-button.active svg .stroke {
  stroke: var(--color-primary);
}

.filter-tray-button .tray-text {
  pointer-events: none;
}

.filters-tray-button * {
  pointer-events: none;
}

fieldset.filters {
  border: none;
  display: none;
  width: 100%;
}

fieldset.filters .group-title {
  font-size: smaller;
  color: var(--color-text);
  font-weight: bold;
  margin: 0 0 5px;
  padding: 20px 0 20px;
  width: 100%;
  border-bottom: 1px solid var(--color-border-ui);
}

fieldset.filters label {
  display: flex;
  align-items: center;
  font-size: smaller;
  margin: 10px 0 5px;
  color: var(--color-text-light);
}

fieldset.filters .filter-group {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  width: 100%;
}

fieldset.filters .form-row {
  justify-content: space-between;
}

input[type=date] {
  font-family: sans-serif;
}

fieldset.filters input,
fieldset.filters select {
  height: 40px;
  padding: 0 5px;
  display: flex;
  align-items: center;
  color: var(--color-text);
  background: var(--color-body);
  border: 1px solid var(--color-border-ui);
  border-radius: var(--ui-radius);
  outline: none;
}

fieldset.filters input:focus,
fieldset.filters select:focus {
  border-color: var(--color-primary);
}

.news-filters.active #filter-button {
  background: var(--color-primary-light);
}

.news-filters.active fieldset.filters {
  display: block;
}

#filter-date-apply {
  margin-top: 15px;
  padding: 15px;
  width: 100%;
  border-radius: var(--ui-radius);
  border: 1px solid transparent;
  background: var(--color-primary-light);
  color: var(--color-text);
  -webkit-appearance: none;
          appearance: none;
  outline: none;
  cursor: pointer;
  z-index: 1;
}

#filter-date-apply:hover {
  border: 1px solid var(--color-primary);
  background: var(--color-body);
  color: var(--color-primary);
}

#select-theme {
  height: 0;
  width: 0;
  visibility: hidden;
}

#select-theme + label {
  cursor: pointer;
  width: 100%;
  height: 40px;
  background: var(--color-primary-light);
  margin: 10px auto 0;
  display: flex;
  align-items: center;
  border-radius: 100px;
  position: relative;
}

#select-theme + label:after {
  content: "";
  background: var(--color-primary);
  width: 30px;
  height: 30px;
  border-radius: 50%;
  position: absolute;
  top: 5px;
  left: 4px;
  transition: cubic-bezier(0.785, 0.135, 0.15, 0.86) 320ms;
}

#select-theme + label:before {
  content: "Dark";
  position: absolute;
  top: 0;
  bottom: 0;
  left: 15px;
  right: 15px;
  color: var(--color-primary);
  display: flex;
  align-items: center;
  justify-content: flex-start;
  z-index: 1;
}

#select-theme:checked + label {
  background: var(--color-primary-light);
}

#select-theme:checked + label:after {
  left: calc(100% - 5px);
  transform: translate3d(-100%, 0, 0);
}

#select-theme + label:before {
  content: "Day";
  justify-content: flex-end;
}

#select-theme:checked + label:before {
  content: "Night";
  justify-content: flex-start;
}

/* 
    Media Queries
*/
@media only screen and (max-width: 767px) {
  .news-filters {
    border-left: none;
    background: var(--color-border-ui);
    top: initial;
    bottom: 0;
    left: 0;
    right: 0;
    width: 100%;
    height: var(--ui-tray-height);
    padding: 0;
    display: block;
  }

  .filters-tray {
    position: fixed;
    bottom: 0;
    left: 0;
    right: 0;
    width: 100vw;
    height: inherit;
    display: grid;
    border-top: 1px solid var(--color-border-ui);
    grid-template-columns: repeat(3, 1fr);
  }

  #filter-button {
    display: none;
  }

  fieldset.filters {
    position: static;
    left: 0;
    bottom: 0;
    padding: 0;
    display: block;
    height: inherit;
  }

  .group-title {
    display: none;
  }

  fieldset.filters .filter-group {
    display: flex;
    align-items: center;
    justify-content: center;
    position: fixed;
    left: 0;
    right: 0;
    width: 100vw;
    height: 15vh;
    background: var(--color-body-dim);
    bottom: var(--ui-tray-height);
    padding: var(--ui-gutter);
    z-index: -1;
    display: none;
    grid-template-columns: 80px calc(100% - 80px);
    box-shadow: 0 -10px 50px rgba(0, 0, 0, 0.33);
  }

  fieldset.filters .filter-group[data-name=more] {
    height: 60vh;
  }

  fieldset.filters .filter-group[data-name=more] .group-title {
    display: block;
    grid-column: span 2;
    padding: 10px 0;
    margin: 0;
  }

  fieldset.filters label {
    margin: 0;
  }

  fieldset.filters .filter-group.active {
    display: grid;
  }

  fieldset.filters input,
fieldset.filters select {
    width: 100%;
  }

  #filter-date-apply {
    margin-top: 0;
    width: 100%;
    grid-column: span 2;
  }

  fieldset.filters #select-theme {
    margin: 0;
    padding: 0;
    grid-column: span 2;
    display: none;
  }

  fieldset.filters #select-theme + label {
    grid-column: span 2;
    margin: 0;
  }
}
