  border-left: none;
  color: var(--color-text);
  cursor: pointer;
  border-radius: 0 var(--ui-radius) var(--ui-radius) 0;
  font-size: 0;
  outline: none;
  display: flex;
  align-items: center;
  justify-content: center;
}

/* 
    #news-form While Focused State 
*/
.is-focused #select-categories {
  background: var(--color-primary-light);
  border-color: var(--color-primary);
}

.is-focused #keywords {
  background: var(--color-primary-light);
  border-color: var(--color-primary);
}

.is-focused #search-button {
  border-left-color: var(--color-primary);
}

.is-focused #search-button {
  border-color: var(--color-primary);
}

.is-focused #search-button .icon {
  stroke: var(--color-primary);
}

/*
    News Loading
*/
.news-loading {
  width: 32px;
  height: 32px;
  position: absolute;
  opacity: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: opacity 0.15s ease;
  -webkit-user-select: none;
          user-select: none;
  background: var(--color-body);
  pointer-events: none;
  z-index: 1;
}

.news-loading:after {
  content: " ";
  position: absolute;
  width: 32px;
  height: 32px;
  border-radius: 50%;
  border: 2px solid var(--color-primary);
  border-color: var(--color-primary) transparent var(--color-primary) transparent;
  animation: ring-loading 1.2s linear infinite;
}

.news-loading:before {
  content: " ";
  position: absolute;
  width: 24px;
  height: 24px;
  border-radius: 50%;
  border: 2px solid var(--color-primary);
  border-color: var(--color-primary) transparent var(--color-primary) transparent;
  animation: ring-loading 1.2s linear infinite;
}

@keyframes ring-loading {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}
/*
    While Page is Loading
*/
.is-loading .news-loading {
  opacity: 1;
}

/*
    Pagination
*/
.news-pagination {
  width: 100%;
  position: relative;
  display: grid;
  grid-template-columns: 1fr 1fr;
  grid-gap: 15px;
  padding: 30px calc(var(--ui-gutter) + var(--ui-filters-width)) 30px var(--ui-gutter);
  transition: opacity 0.6s ease;
}

.news-pagination button {
  flex: 1;
  padding: 15px 40px;
  border: 1px solid transparent;
  border-radius: var(--ui-radius);
  background: var(--color-primary-light);
  color: var(--color-text);
  cursor: pointer;
  outline: none;
  white-space: nowrap;
}

.news-pagination button:first-child {
  margin-right: 5px;
}

.news-pagination button:last-child {
  margin-left: 5px;
}

.news-pagination button:not([disabled]):hover {
  background: var(--color-primary-light);
  border-color: var(--color-primary);
  color: var(--color-primary);
}

.news-pagination button[disabled] {
  background: transparent;
  border: 1px dotted var(--color-border-ui);
  color: var(--color-border-ui);
  cursor: default;
}

#news-current-page {
  overflow: hidden;
  padding: 15px;
  color: var(--color-text);
  grid-column: span 2;
  text-align: center;
  font-size: x-small;
}

#news-current-page::before {
  content: "Page ";
}

/*
    While Page is Loading
*/
.is-loading .news-pagination {
  opacity: 0;
}

/*
    Media Query
*/
@media only screen and (max-width: 767px) {
  .news-pagination {
    grid-gap: 5px;
    padding: 10px 10px calc(var(--ui-tray-height) + 10px);
  }
}
/*
    News Results
*/
#news-results {
  padding: 30px calc(var(--ui-gutter) + var(--ui-filters-width)) 0 var(--ui-gutter);
  transition: opacity 0.6s ease;
}

#news-results:empty {
  min-height: 60vh;
  justify-content: center;
  display: flex;
  align-items: center;
}

#news-results:empty::before {
  text-align: center;
  content: "One moment please...";
  font-size: 1em;
}

#news-results[style] + .news-pagination,
#news-results:empty + .news-pagination {
  opacity: 0;
  pointer-events: none;
}

/*
    Active filters 
*/
.is-active-filters#news-results {
  filter: blur(3px);
  opacity: 0.6;
}

.is-active-filters#news-results * {
  pointer-events: none;
}

/*
    Media Query
*/
@media only screen and (max-width: 767px) {
  #news-results {
    padding-top: 10px;
    padding-right: 10px;
  }
}
